import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Spinner, Alert, Form,Button,Card } from 'react-bootstrap';
import CarCard from '../components/CarCard';
import HeroBanner from '../components/HeroBanner';


import axios from 'axios';

const popularBrands = [
  { name: 'Maruti', logo: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRCHPEZfCWoAHjrN-FMbkVbTnoBnbZeZvY0HQ&s' },
  { name: 'Hyundai', logo: 'data:image/jpeg;base64,' },
  { name: 'Honda', logo: 'https://upload.wikimedia.org/wikipedia/commons/thumb/7/7b/Honda_Logo.svg/512px-Honda_Logo.svg.png' },
  { name: 'Tata', logo: 'https://www.purppledesigns.com/wp-content/uploads/2023/11/images-1.jpg' },
  { name: 'Toyota', logo: 'data:image/jpeg;base64,' },
  { name: 'Kia', logo: 'data:image/jpeg;base64,' },
];

const Home = () => {
     // State to store fetched car data and filter options
  const [cars, setCars] = useState([]);
  const [carTypes, setCarTypes] = useState([]);
  const [carMakes, setCarMakes] = useState([]);
  const [carYears, setCarYears] = useState([]);

    // State for selected filters
  const [selectedType, setSelectedType] = useState('');
  const [selectedMake, setSelectedMake] = useState('');
  const [selectedYear, setSelectedYear] = useState('');

      // State for loading, error, and pagination
  const [loading, setLoading] = useState(true);
  const [apiError, setApiError] = useState('');
  const [currentPage, setCurrentPage] = useState(0);
  const [hasMore, setHasMore] = useState(true);


 // Fetch car list and filter options on initial render and page change
  useEffect(() => {
  fetchCarsFromAPI(currentPage);
  fetchCarTypesFromAPI();
  fetchCarMakesFromAPI(); 
  fetchCarYearsFromAPI(); 
}, [currentPage]);


 // Fetch paginated car list
 const fetchCarsFromAPI = async (pageNumber = 0) => {
    setLoading(true);
    const options = {
      method: 'GET',
      url: 'https://car-data.p.rapidapi.com/cars',
      params: { limit: '10', page: pageNumber.toString() },
      headers: {
        'x-rapidapi-key': 'c98690a536msh6c5d8d0210b22d1p112baejsn1f8b4527433d',
        'x-rapidapi-host': 'car-data.p.rapidapi.com',
      },
    };

    try {
      const response = await axios.request(options);
      const data = response.data;

      if (data.length < 10) setHasMore(false);
      else setHasMore(true);

      const formatted = data.map((car, index) => ({
        id: `${car.make}_${car.model}_${car.year}_${pageNumber}_${index}`,
        name: `${car.make} ${car.model}`,
        brand: car.make,
        year: car.year,
        price: 500000 + (pageNumber * 100000) + index * 10000,
        location: 'India',
        image: `https://source.unsplash.com/400x250/?${car.make}-${car.model}`,
      }));

      setCars(formatted);// Set fetched and formatted car data
    } catch (error) {
      console.error('API fetch error:', error);
      setApiError('Failed to fetch car data. Please try again later.');
    } finally {
      setLoading(false);
    }
  };


// Fetch car types for filter dropdown
  const fetchCarTypesFromAPI = async () => {
    try {
      const options = {
        method: 'GET',
        url: 'https://car-data.p.rapidapi.com/cars/types',
        headers: {
          'x-rapidapi-key': 'c98690a536msh6c5d8d0210b22d1p112baejsn1f8b4527433d',
          'x-rapidapi-host': 'car-data.p.rapidapi.com',
        },
      };
      const response = await axios.request(options);
      setCarTypes(response.data);
    } catch (error) {
      console.error('Error fetching car types:', error);
    }
  };
 // Fetch car makes for filter dropdown
  const fetchCarMakesFromAPI = async () => {
    try {
      const options = {
        method: 'GET',
        url: 'https://car-data.p.rapidapi.com/cars/makes',
        headers: {
          'x-rapidapi-key': 'c98690a536msh6c5d8d0210b22d1p112baejsn1f8b4527433d',
          'x-rapidapi-host': 'car-data.p.rapidapi.com',
        },
      };
      const response = await axios.request(options);
      setCarMakes(response.data);
    } catch (error) {
      console.error('Error fetching car makes:', error);
    }
  };
 // Fetch car years for filter dropdown
  const fetchCarYearsFromAPI = async () => {
    try {
      const options = {
        method: 'GET',
        url: 'https://car-data.p.rapidapi.com/cars/years',
        headers: {
          'x-rapidapi-key': 'c98690a536msh6c5d8d0210b22d1p112baejsn1f8b4527433d',
          'x-rapidapi-host': 'car-data.p.rapidapi.com',
        },
      };
      const response = await axios.request(options);
      setCarYears(response.data);
    } catch (error) {
      console.error('Error fetching car years:', error);
    }
  };
// Filter cars based on selected type, make, and year
  const filteredCars = cars.filter((car) => {
    return (
      (!selectedType || car.name.toLowerCase().includes(selectedType.toLowerCase())) &&
      (!selectedMake || car.brand === selectedMake) &&
      (!selectedYear || car.year === parseInt(selectedYear))
    );
  });

  return (
   
    // <Container className="my-4">
      <Container className="mt-4">
       <h2 className="mb-4">Explore Cars</h2>
    <HeroBanner />
      <h2 className="mb-4">Browse Cars</h2>

      {/* Filters */}
      <Row className="mb-4">
        <Col md={4}>
          <Form.Group controlId="carTypeFilter">
            <Form.Label>Filter by Type</Form.Label>
            <Form.Select
              value={selectedType}
              onChange={(e) => setSelectedType(e.target.value)}
            >
              <option value="">All Types</option>
              {carTypes.map((type, idx) => (
                <option key={idx} value={type}>
                  {type}
                </option>
              ))}
            </Form.Select>
          </Form.Group>
        </Col>

        <Col md={4}>
          <Form.Group controlId="carMakeFilter">
            <Form.Label>Filter by Make</Form.Label>
            <Form.Select
              value={selectedMake}
              onChange={(e) => setSelectedMake(e.target.value)}
            >
              <option value="">All Makes</option>
              {carMakes.map((make, idx) => (
                <option key={idx} value={make}>
                  {make}
                </option>
              ))}
            </Form.Select>
          </Form.Group>
        </Col>

        <Col md={4}>
          <Form.Group controlId="carYearFilter">
            <Form.Label>Filter by Year</Form.Label>
            <Form.Select
              value={selectedYear}
              onChange={(e) => setSelectedYear(e.target.value)}
            >
              <option value="">All Years</option>
              {carYears.map((year, idx) => (
                <option key={idx} value={year}>
                  {year}
                </option>
              ))}
            </Form.Select>
          </Form.Group>
        </Col>
      </Row>

      

      {/* Loader and Error */}
      {loading && <Spinner animation="border" />}
      {apiError && <Alert variant="danger">{apiError}</Alert>}

      {/* Car List */}
      <Row>
        {filteredCars.map((car) => (
          <Col key={car.id} md={4} className="mb-4">
            <CarCard car={car} />
          </Col>
        ))}

      </Row>
     {/* Pagination Buttons */}
      <div className="d-flex justify-content-between mt-4">
        <Button
          variant="secondary"
          onClick={() => setCurrentPage(prev => Math.max(prev - 1, 0))}
          disabled={currentPage === 0}
        >
          Previous
        </Button>

        <span className="align-self-center">Page {currentPage + 1}</span>

        <Button
          variant="primary"
          onClick={() => setCurrentPage(prev => prev + 1)}
          disabled={!hasMore}
        >
          Next
        </Button>
        
      </div>

       <h3 className="mt-5 mb-3">Popular Brands</h3>
      <Row>
     {popularBrands.map((brand, index) => (
          <Col xs={6} md={4} lg={2} className="mb-4" key={index}>
             <Card className="h-100 shadow-sm border-0 text-center">
               <Card.Img
                 variant="top"
                 src={brand.logo}
                 alt={brand.name}
                 style={{ height: '80px', objectFit: 'contain', marginTop: '15px' }}
               />
               <Card.Body>
                 <Card.Title style={{ fontSize: '1rem' }}>{brand.name}</Card.Title>
               </Card.Body>
           </Card>
           </Col>
         ))}
       </Row>
      

    </Container>
  );
};

export default Home;
